      bcanvas.setDimensions({ width: 180, height: 300 });
      var canvas = new fabric.Canvas("tcanvas");
      canvas.setDimensions({ width: 180, height: 300 });
      console.log("--------- test ------- ", canvas)
      // create a rect object
      var deleteIcon =
        "data:image/svg+xml,%3C%3Fxml version='1.0' encoding='utf-8'%3F%3E%3C!DOCTYPE svg PUBLIC '-//W3C//DTD SVG 1.1//EN' 'http://www.w3.org/Graphics/SVG/1.1/DTD/svg11.dtd'%3E%3Csvg version='1.1' id='Ebene_1' xmlns='http://www.w3.org/2000/svg' xmlns:xlink='http://www.w3.org/1999/xlink' x='0px' y='0px' width='595.275px' height='595.275px' viewBox='200 215 230 470' xml:space='preserve'%3E%3Ccircle style='fill:%23F44336;' cx='299.76' cy='439.067' r='218.516'/%3E%3Cg%3E%3Crect x='267.162' y='307.978' transform='matrix(0.7071 -0.7071 0.7071 0.7071 -222.6202 340.6915)' style='fill:white;' width='65.545' height='262.18'/%3E%3Crect x='266.988' y='308.153' transform='matrix(0.7071 0.7071 -0.7071 0.7071 398.3889 -83.3116)' style='fill:white;' width='65.544' height='262.179'/%3E%3C/g%3E%3C/svg%3E";

      var img = document.createElement("img");
      img.src = deleteIcon;

      fabric.Object.prototype.transparentCorners = false;
      fabric.Object.prototype.cornerColor = "blue";
      fabric.Object.prototype.cornerStyle = "circle";

      function AddText(texter) {
        var status = $("#imageBack").hasClass("active");
        console.log("---------- called -------- ")
        var rect = new fabric.Text(texter, {
          left: 50,
          top: 50,
          fontFamily: "helvetica",
          angle: 0,
          fill: "#000000",
          scaleX: 1,
          scaleY: 1,
          fontWeight: "",
          hasRotatingPoint: true,
        });
        if (status == true) {
          bcanvas.add(rect);
          bcanvas.item(canvas.item.length - 1).hasRotatingPoint = true;
          bcanvas.requestRenderAll();
           bcanvas.setActiveObject(rect);
        } else {
          canvas.add(rect);
          canvas.item(canvas.item.length - 1).hasRotatingPoint = true;
          canvas.requestRenderAll();
           canvas.setActiveObject(rect);
        }
      }

      fabric.Object.prototype.controls.deleteControl = new fabric.Control({
        x: 0.5,
        y: -0.5,
        offsetY: 16,
        cursorStyle: "pointer",
        mouseUpHandler: deleteObject,
        render: renderIcon,
        cornerSize: 24,
      });

      function deleteObject(eventData, transform) {
        var target = transform.target;
        var canvas = target.canvas;
        canvas.remove(target);
        canvas.requestRenderAll();
      }

      function renderIcon(ctx, left, top, styleOverride, fabricObject) {
        var size = this.cornerSize;
        ctx.save();
        ctx.translate(left, top);
        ctx.rotate(fabric.util.degreesToRadians(fabricObject.angle));
        ctx.drawImage(img, -size / 2, -size / 2, size, size);
        ctx.restore();
      }

      $("#add-textBox").on("click", function () {
        var text = $("#text-string").val();
        console.log("------------- add p-tte ----")
        if (text == "") {
        } else {
          AddText(text);

          $("#font-editor").css({ display: "block" });
        }
      });
      $("#text-string").keyup(function(){
        var status = $("#imageBack").hasClass("active");
		if (status == true) {
		  var activeObject = bcanvas.getActiveObject();
				      if (activeObject && activeObject.type === 'text') {
				    	  activeObject.text = this.value;
				    	  bcanvas.renderAll();
				      }
		}else{
		  var activeObject = canvas.getActiveObject();
				      if (activeObject && activeObject.type === 'text') {
				    	  activeObject.text = this.value;
				    	  canvas.renderAll();
				      }
		}
      });

      $("#bold").click(function () {
        var status = $("#imageBack").hasClass("active");

        if (status == true) {
          var activeObject = bcanvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.fontWeight =
              activeObject.fontWeight == "bold" ? "" : "bold";

            bcanvas.renderAll();
          }
        } else {
          var activeObject = canvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.fontWeight =
              activeObject.fontWeight == "bold" ? "" : "bold";
            canvas.renderAll();
          }
        }

        if (activeObject && activeObject.type === "text") {
          canvas.renderAll();
        }
      });
      $("#FontRange").on("change", function () {
        var size = $(this).val();
        var status = $("#imageBack").hasClass("active");

        if (status == true) {
          var activeObject = bcanvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.fontSize = size;

            bcanvas.renderAll();
          }
        } else {
          var activeObject = canvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.fontSize = size;

            canvas.renderAll();
          }
        }
      });
      $("#italic").click(function () {
        var status = $("#imageBack").hasClass("active");

        if (status == true) {
          var activeObject = bcanvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.fontStyle =
              activeObject.fontStyle == "italic" ? "" : "italic";

