      $("#TextColor").on("change", function () {
        var status = $("#imageBack").hasClass("active");
        var color = $(this).val();
        if (status == true) {
          var activeObject = bcanvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.set({ fill: color });
            bcanvas.renderAll();
          }
        } else {
          var activeObject = canvas.getActiveObject();

          if (activeObject && activeObject.type === "text") {
            activeObject.set({ fill: color });
            canvas.renderAll();
          }
        }
      });

      function setFont(fontDaata) {
        var status = $("#imageBack").hasClass("active");
        if (status == true) {
          var activeObject = bcanvas.getActiveObject();
          if (activeObject && activeObject.type === "text") {
            activeObject.set({ fontFamily: fontDaata });
            bcanvas.renderAll();
          }
        } else {
          var activeObject = canvas.getActiveObject();
          if (activeObject && activeObject.type === "text") {
            activeObject.set({ fontFamily: fontDaata });
            canvas.renderAll();
          }
        }
      }

